import React, { useState, useContext } from 'react';
import ContactContext from '../../context/contact/ContactContext';

const ContactForm = () => {
    const contactContext = useContext(ContactContext);
    const { addContact, current } = contactContext;

    useEffect(() => {
        if(current !== null) {
            setContact(current);
        } else {
            setContact({
                name: '',
                email: '',
                phone: '',
                type: 'personal'
            });
        }
    }, [])
    const [contact, setContact] = useState({
        name: '',
        email: '',
        phone: '',
        type: 'personal'
    });

    const { name, email, phone, type } = contact;

    const onChange = event => setContact({ ...contact, [event.target.name]: event.target.value });

    const onSubmit = event => {
        event.preventDefault();

        addContact(contact);

        setContact({
            name: '',
            email: '',
            phone: '',
            type: 'personal'
        });
    };

    return (
        <form onSubmit={onSubmit}>
            <h2 className="text-primary">Add Contact</h2>
            <input 
                type="text" 
                placeholder="name" 
                name="name" 
                value={name} 
                onChange={onChange}
            />
            <input 
                type="email" 
                placeholder="Email" 
                name="email" 
                value={email} 
                onChange={onChange}
            />
            <input 
                type="text" 
                placeholder="Phone" 
                name="phone" 
                value={phone} 
                onChange={onChange}
            />
            <h5>Contact Type</h5>
            <input type="radio" name="type" value="personal" checked={type === 'personal'} onChange={onChange}/>
            Personal{' '}
            <input type="radio" name="type" value="professional" checked={type === 'professional'} />
            Professional
            <div>
                <input type="submit" value="Add Contact" className="btn btn-primary btn-block" onChange={onChange}/>
            </div>
        </form>
    )
}
export default ContactForm;
